import React, { Component } from 'react';

class MessageList extends Component {
  constructor(props) {
    super(props);
//    this.updateMessage = this.updateMessage.bind(this)
    this.submitMessage = this.submitMessage.bind(this)
    this.state = {
        message: '',
        messages: [
          {id:0, text:'first message'},
          {id:1, text: 'second message'},
          {id:2, text: 'third message'},
          {id:3, text: 'fourth message'}
    ]
  }

  this.messageRef = this.props.firebase.database().ref('messages');
  }

  componentDidMount(){
    this.messageRef.on('child_added', snapshot => {
      const message = snapshot.val();
      message.key = snapshot.key;
      this.setState({ messages: this.state.messages.concat( message ) })
    });
  }
  updateMessage(e){
    console.log('updateMessage: '+ e.target.value)
    this.setState({
      message: e.target.value
    })
  }

  createMessage(e){
    const currentMessage = this.state.messages.map( (message, index) => {
      return (
        this.messageRef.push({
          name: currentMessage

        }))
      })
    }

    submitMessage(e){
      console.log('submitMessage: '+ this.state.message)
    }

  render() {
    return (
      <form>
            'New Message:'
            <div>
              <ol>
              {this.currentMessage}
              </ol>
            <input onChange={this.updateMessage} type="text" value="Message" />
            <br />
            <button onClick={this.submitMessage}>Submit Message</button>
          </div>
        </form>
    );
  }
}
export default MessageList;
