import React, { Component } from 'react';
import RoomList from './components/RoomList';
import MessageList from './components/MessageList';
import { Route, Link } from 'react-router-dom';


import Home from './components/Home';
import './App.css';
import * as firebase from 'firebase';
import fire from 'firebase';

var config = {
  apiKey: "AIzaSyAfTbKtXZUD0PZzJxGECO3unfiv10ZqX0w",
  authDomain: "bloc-chat-kraman.firebaseapp.com",
  databaseURL: "https://bloc-chat-kraman.firebaseio.com",
  projectId: "bloc-chat-kraman",
  storageBucket: "bloc-chat-kraman.appspot.com",
  messagingSenderId: "596845905245"
};
firebase.initializeApp(config);

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {activeRoom: ""};
    this.activeRoom = this.activeRoom.bind(this);
    this.authListener = this.authListener.bind(this);
  }

activeRoom(room) {
  this.setState({ activeRoom: room })
}

componentDidMount() {
  this.authListener();
  }

    authListener() {
      fire.auth().onAuthStateChanged((user) => {

        if (user) {
          this.setState({ user });
        } else {
          this.setState({ user:null });
        }
      });
    }

render() {
  const showMessages = this.state.activeRoom;
  return (
    <div className="App">
    <nav className="navbar navbar-dark bg-primary">
      <div>{this.state.activeRoom.title || "Bloc Chat"}</div>
    <Link className="nav-bar-link" id="link1" to='/'>Home</Link>
    <Link className="nav-bar-link" id="link2" to='/roomlist'>Rooms</Link>
    </nav>




      <div>
      <RoomList firebase={firebase} activeRoom={this.activeRoom} />
      { showMessages ?
      (<MessageList firebase={firebase} activeRoom={this.state.activeRoom.key}/>)
      : (null)
      }
      </div>
            <main>
               <Route exact path="/" component={Home} />
               <Route path="/roomlist" component={RoomList} />
             </main>

        </div>


  );
}
}

export default App;
