import React, { Component } from 'react';

class RoomList extends Component {
  constructor(props) {
    super(props);
      this.state = {roomName: "", rooms: [], isHidden: true};
      this.roomsRef = this.props.firebase.database().ref("rooms");
      this.handleChange = this.handleChange.bind(this);
      this.createRoom = this.createRoom.bind(this);
  }
  componentDidMount() {
    this.roomsRef.on('child_added', snapshot => {
      const room = snapshot.val();
      room.key = snapshot.key;
      this.setState({ rooms: this.state.rooms.concat(room) })
    });
  }

  handleChange(e) {
    this.setState({ name: e.target.value });
  }

  createRoom(e) {
  		e.preventDefault();
  		const roomName = this.state.name;
  		if (roomName.length >= 1) {
  			this.roomsRef.push({ name: roomName });
  		}
  		this.setState({ name: '' });
  		this.setState({ isHidden: true });
  	}

  selectRoom(room) {
    this.props.activeRoom(room);
  }

  render() {
    const roomForm = (
      <form onSubmit={this.createRoom}>
      <h1>Room</h1>
        <input
        type="text"
        value={this.state.name}
        placeholder="Enter Room Name" 
        onChange={this.handleChange}
        />
        <button className="enter-room" onSubmit={ this.createRoom }>Enter Room</button>
      </form>
    );

    const roomList = this.state.rooms.map( (room) =>
      <li key={room.key} onClick={ () => this.selectRoom(room)}>{room.name}</li>
    );

    return(
      <div>
        <div>{roomForm}</div>
        <div>{roomList}</div>
      </div>
    );
  }
}

export default RoomList;
