import React, { Component } from 'react';

class MessageList extends Component {
  constructor(props) {
    super(props);
    this.state = {
        messages: [
          {id:0, text:'first message'},
          {id:1, text: 'second message'},
          {id:2, text: 'third message'},
          {id:3, text: 'fourth message'}
    ]
  }

  this.messageRef = this.props.firebase.database().ref('messages');
  }

  componentDidMount(){
    this.messageRef.on('child_added', snapshot => {
      const message = snapshot.val();
      message.key = snapshot.key;
      this.setState({ messages: this.state.messages.concat( message ) })
    });
  }


  createMessage(){
    const currentMessage = this.state.messages.map( (message, index) => {
      return (
        <li
        key={message.id}>{message.text}</li>
      )}
    )}

  render() {
    return (
      <form>
            'New Message:'
            <div>
              <ol>
              {this.createMessage}
              </ol>
            <input onChange={this.updateMessage} type="text" value="Message" />
            <br />
            <button>Submit Message</button>
          </div>
        </form>
    );
  }
}
export default MessageList;
