import React, { Component } from 'react';
import RoomList from './components/RoomList';
import MessageList from './components/MessageList';
import { Route, Link } from 'react-router-dom';
import Modal from './components/Modal';
import User from './components/User';
import Home from './components/Home';
import './App.css';
import * as firebase from 'firebase';
import fire from 'firebase';

var config = {
  apiKey: "AIzaSyAfTbKtXZUD0PZzJxGECO3unfiv10ZqX0w",
  authDomain: "bloc-chat-kraman.firebaseapp.com",
  databaseURL: "https://bloc-chat-kraman.firebaseio.com",
  projectId: "bloc-chat-kraman",
  storageBucket: "bloc-chat-kraman.appspot.com",
  messagingSenderId: "596845905245"
};
firebase.initializeApp(config);

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      activeRoom: "",
      activeRoomId: '',
      user: null,
  };
    this.activeRoom = this.activeRoom.bind(this);
    this.setUserName = this.setUserName.bind(this);
    this.authListener = this.authListener.bind(this);
  }
  state = { show: false }

  showModal = () => {
    this.setState({ show: true });
  }

  hideModal = () => {
    this.setState({ show: false });
  }

activeRoom(room) {
  this.setState({
      activeRoom: room.name,
      activeRoomId: room.key,
    });
}

setUserName(user) {
   this.setState({
     user: user,
   });
 }

componentDidMount() {
  this.authListener();
  }

    authListener() {
      fire.auth().onAuthStateChanged((user) => {

        if (user) {
          this.setState({ user });
        } else {
          this.setState({ user:null });
        }
      });
    }

render() {

  return (
    <section className="wrapper">
    <main>
       <h1>React Modal</h1>
       <Modal show={this.state.show} handleClose={this.hideModal} >
       </Modal>
       <button type='button' onClick={this.showModal}>Open</button>
     </main>
            <header className="app-title">
            <nav className="navbar navbar-dark bg-primary">

            <button type="button" class="btn btn-outline-light">
            <Link className="nav-link" id="link1" to='/'>Home</Link>
            </button>
            <button type="button" class="btn btn-outline-light">
            <Link className="nav-link" id="link2" to='/roomlist'>Rooms</Link>
            </button>
            </nav>
              <h1>Bloc Chat</h1>
              <h2>Current Room: {this.state.activeRoom}</h2>
              <User />
              </header>
              <section>

              <section className="rows">
                <section className="room-list">
                  <RoomList firebase={firebase} setActiveRoom={this.setActiveRoom} />
                </section>

                <section className="message-list">
                  <MessageList
                    firebase={firebase}
                    activeRoomId={this.state.activeRoomId}
                    user={this.state.user}
                  />
                </section>

              </section>
              </section>
              <main>
              <Route exact path="/" component={Home} />
              <Route path="/roomlist" component={RoomList} />
              </main>
              </section>


  );
}
}

export default App;
