import React, { Component } from 'react';

class RoomList extends Component {
  constructor(props) {
    super(props);
      this.state = {
       rooms: [],
       newRoomName: '',
    };
    this.roomsRef = this.props.firebase.database().ref('Rooms');
    this.handleChange = this.handleChange.bind(this);
    this.handleCreateRoom = this.handleCreateRoom.bind(this);
  //  this.handleRoomClick = this.handleRoomClick.bind(this);
  }

   componentDidMount() {
       this.roomsRef.on('child_added', snapshot => {
           const room = snapshot.val();
           room.key = snapshot.key;
           this.setState({ rooms: this.state.rooms.concat( room ) })
       });
   }

   handleChange(e) {
       this.setState({ newRoomName: e.target.value });
   }

   handleCreateRoom(e) {
       e.preventDefault();
       if(!this.state.newRoomName) { return }
       const newRoom = { name: this.state.newRoomName };
       this.roomsRef.push(newRoom);
       this.setState({ newRoomName: '' });
   }

   handleRoomClick(room) {
        this.props.setActiveRoom(room);
}

render() {
    return (

        <div className="RoomList">
          {this.state.rooms.map ((room, index) =>
            <div
              key={room.key} onClick={()=> this.props.setActiveRoom(room)}>
              {room.name}
            </div>
          )}
          <form onSubmit={ (e) => this.handleCreateRoom(e) }>
    	   		<input type="text" name="New Room" value={this.state.newRoomName} placeholder="Enter Chat Room" onChange={ (room) => this.handleChange(room) } />
    	   		<input type="submit" />
          </form>
        </div>
    );
  }
}

export default RoomList;
