import React, { Component } from 'react';

class MessageList extends Component {
  constructor(props) {
    super(props);
    this.state = { messages: [
      {id:0, text:'first message'},
      {id:1, text: 'second message'},
      {id:2, text: 'third message'},
      {id:3, text: 'fourth message'}
    ] };

    this.messageRef = this.props.firebase.database().ref('messages');
  }

  componentDidMount(){
    this.messageRef.on('child_added', snapshot => {
      const message = snapshot.val();
      message.key = snapshot.key;
      this.setState({ messages: this.state.messages.concat( message ) })
    });
  }

  createMessage(e){
  const newMessageName = this.state.newMessageName;
  e.preventDefault()
  this.messagesRef.push({
    name: newMessageName
  });
}

handleChange(e){
  this.setState({ newMessageName: e.target.value });
}

  render() {
    return (
      <section>
      {
        this.state.messages.map( (message, index) =>
        <li key={message.id} > {message.text}</li>,
        )
      }

      <form onSubmit={(e) => this.createMessage(e)}>
          <label>
            New Message:
            <div>
              <ol>
                <li>message 1</li>
                <li>message 2</li>
                <li>message 3</li>
                <li>message 4</li>
              </ol>
            <input type="text" value={this.state.newMessageName} onChange={ e => this.handleChange(e) }/>
            </div>
          </label>
          <input type="submit" value="Submit" />
        </form>
      </section>
    );
  }
}

export default MessageList;
