import React, { Component } from 'react';


class MessageList extends Component {
  constructor(props) {
    super(props);
      this.state = { messages:  [{username: "", content: "", sentAt: "", roomId: ""},], newMessage:""};
      this.messagesRef = this.props.firebase.database().ref("messages");
      this.handleChange = this.handleChange.bind(this);
      this.createMessage = this.createMessage.bind(this);
  }

  handleChange(e) {
    e.preventDefault();
    this.setState({newMessage: e.target.value});
  }

  createMessage(e) {
      e.preventDefault();
      this.messagesRef.push({
        username: this.props.user ? this.props.user.displayName : 'Guest',
        content: this.state.newMessage,
        sentAt: this.props.firebase.database.ServerValue.TIMESTAMP,
        roomId: this.props.activeRoomId,
      })
      this.setState({newMessage: ''});
    }


  componentDidMount() {
    this.messagesRef.on('child_added', snapshot => {
      const message = snapshot.val();
      message.key = snapshot.key;
      this.setState({ messages: this.state.messages.concat(message) })
    });
  }

  formatTime(time) {
  if (time){
     const date = new Date(time);
     const hour = date.getHours();
     const min = date.getMinutes() < 10 ? '0' + date.getMinutes() : date.getMinutes();
     const sec = date.getSeconds() < 10 ? '0' + date.getSeconds() : date.getSeconds();
     const newTime = hour + ':' + min + ':' + sec;
     return newTime;
   }
}


    render() {
        const activeRoomId = this.props.activeRoomId
       return (
        <section>
           <section>
               <div className="activeRoom">
                   Current Room: {this.props.activeRoom.name}
               </div>
               <div>
                   {this.state.messages
                       .filter(message => {
                           return message.roomId === this.props.activeRoom.name;
                       })
                       .map((message, i) => (
                  <ul key={i}>
                    <li> Message content: {message.content} </li>
                    <li> Room ID: {message.roomId} </li>
                    <li> Username: {message.username} </li>
                    <li> Sent at: {this.formatTime(message.sentAt)} </li>
                    </ul>
            ))}
            </div>
            </section>
          )
        }
        <section>
          <form className="add-message" onSubmit={this.createMessage}>
            <input
              type="text"
              placeholder="Write Your Message Here"
              value={this.state.newMessage}
              onChange={this.handleChange}
            />
            <input type="submit" value="Send" />
          </form>
        </section>
        </section>

      )
    }
  }

export default MessageList;
