import React, { Component } from 'react';
import { Grid } from 'react-bootstrap';
import './App.css';
import RoomList from './components/RoomList';
import MessageList from './components/MessageList';
import Container from 'react-bootstrap/Container'
import Col from 'react-bootstrap/Col'
import * as firebase from 'firebase';

var config = {
  apiKey: "AIzaSyAfTbKtXZUD0PZzJxGECO3unfiv10ZqX0w",
  authDomain: "bloc-chat-kraman.firebaseapp.com",
  databaseURL: "https://bloc-chat-kraman.firebaseio.com",
  projectId: "bloc-chat-kraman",
  storageBucket: "bloc-chat-kraman.appspot.com",
  messagingSenderId: "596845905245"
};
firebase.initializeApp(config);

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
     activeRoom: ''
   }
 }

   handlerSetActiveRoom = (room) => {
     this.setState({ activeRoom: room });
  }

    render() {
     return (
       <Container>
       <Col>1 of 2</Col>
       <div className="App">
         <RoomList className="room-list-container"
           firebase = {firebase} activeRoom={this.state.activeRoom} toSetActiveRoom={this.handlerSetActiveRoom}
         />
         <Col>2 of 2</Col>
         <MessageList className="message-list-container"
          firebase = {firebase} activeRoom={this.state.activeRoom}
          />
       </div>
       </Container>
     );
   }
 }

 export default App;
