import React, { Component } from 'react';
import './App.css';
import RoomList from './components/RoomList';
import MessageList from './components/MessageList';
import User from './components/User';


import * as firebase from 'firebase';


var config = {
  apiKey: "AIzaSyAfTbKtXZUD0PZzJxGECO3unfiv10ZqX0w",
  authDomain: "bloc-chat-kraman.firebaseapp.com",
  databaseURL: "https://bloc-chat-kraman.firebaseio.com",
  projectId: "bloc-chat-kraman",
  storageBucket: "bloc-chat-kraman.appspot.com",
  messagingSenderId: "596845905245"
};
firebase.initializeApp(config);


class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      activeRoom: '',
      activeRoomId: '',
      user: 'null',
    };
    this.setActiveRoom = this.setActiveRoom.bind(this);
    this.setUserName = this.setUserName.bind(this);
  }

  componentDidMount() {
   firebase.auth().onAuthStateChanged( (user) => {
     if (user) {
       this.setUser( user );
     }
     else {
       this.setUser( 'guest' );
     }
   } );
 }

  setActiveRoom(room) {
    this.setState({
      activeRoom: room.name,
    });
  }

  setUserName(user) {
    this.setState({
      user: user,
    });
  }

  render() {
    return (
      <section>
      <section className="container-fluid">
        <header className="jumbotron">
          <h1 class="display-4">Delaware's Community Garden</h1>

          <User
                firebase={ firebase }
                user={ this.state.setUserName }
                signInGoogle={ () => this.signIn() }
                signOutGoogle={ () => this.signOut() }
              />
            </header>
            <RoomList
              firebase={ firebase }
              activeRoom={ this.state.activeRoom }
              handleSelectRoom={ ( e ) => this.setActiveRoom( e ) }
            />

            <MessageList
              firebase={ firebase }
              setUserName={ this.state.user }
              setActiveRoom={ this.state.activeRoom }
              />
              </section>
              </section>
    );
  }
}

 export default App;
