import React, { Component } from 'react';




class User extends Component {
  constructor(props) {
    super(props);
    this.signInGoogle = this.signInGoogle.bind(this)
    this.signOutGoogle = this.signOutGoogle.bind(this)
  }


    componentDidMount() {
      this.props.firebase.auth().onAuthStateChanged(user => {
        this.props.setUserName(user);
      });
  }


  signInGoogle() {
    const provider = new this.props.firebase.auth.GoogleAuthProvider();
    this.props.firebase.auth().signInWithPopup(provider);
}

  signOutGoogle() {
    this.props.firebase.auth.signOutGoogle();
 }


  render() {
    return(
      <div className="google-sign">
        <section className="btn-group">
         <button type="button" onClick={this.signInGoogle}>Sign In with Google</button>
         <button type="button" onClick={this.signOutGoogle}>Sign Out</button>

         <section className="display-username">
            Sign In As:
            {this.props.user ? this.props.setUserName : ''}
          </section>
          </section>

        </div>

  )
}
}

export default User;
