import React, { Component } from 'react';
import RoomList from './components/RoomList';
import MessageList from './components/MessageList';
import User from './components/User';

import './App.css';
import * as firebase from 'firebase';
import fire from 'firebase';

var config = {
  apiKey: "AIzaSyAfTbKtXZUD0PZzJxGECO3unfiv10ZqX0w",
  authDomain: "bloc-chat-kraman.firebaseapp.com",
  databaseURL: "https://bloc-chat-kraman.firebaseio.com",
  projectId: "bloc-chat-kraman",
  storageBucket: "bloc-chat-kraman.appspot.com",
  messagingSenderId: "596845905245"
};
firebase.initializeApp(config);

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      activeRoom: "",
      activeRoomId: '',
      newUser: null,
  };
    this.activeRoom = this.activeRoom.bind(this);
    this.setUserName = this.setUserName.bind(this);
    this.authListener = this.authListener.bind(this);
  }

activeRoom(room) {
  this.setState({
      activeRoom: room.name,
      activeRoomId: room.key,
    });
}

setUser(newUser) {
    if(newUser !== null) {
      this.setState({ username: newUser.displayName });
    }
    else {
      this.setState({ username: null });
    }
  }


render() {

  return (
    <div className="App">
          <header className="App-header">
            <h1 className="App-title">Bloc Chat</h1>
            <User firebase={firebase}
                  setUser={(newUser) => this.setUser(newUser)}
                  username={this.state.username} />
          </header>
          <section className="App-room-content">
            <RoomList activeRoom={this.state.activeRoomKey} handleClickRoom={(e) => this.handleClickRoom(e)} firebase={firebase}></RoomList>
            <section className="App-room-chat-area">
              <MessageList activeRoom={this.state.activeRoomKey}
                           activeRoomName={this.state.activeRoomName}
                           firebase={firebase}
                           username={this.state.username}></MessageList>
                           </section>
                           </section>
                         </div>


  );
}
}

export default App;
