import React, { Component } from 'react';

class MessageList extends Component {
  constructor(props) {
    super(props);

    this.state = {
      messages: [],
      newMessage: '',
      roomId: '',
      user: '',
      sendAt: ''
    };
    this.messageRef = this.props.firebase.database().ref('messages');
  }

  componentDidMount() {
    this.messageRef.on('child_added', snapshot => {
      const msg = snapshot.val();
      msg.key = snapshot.key;
      this.setState({messages: this.state.messages.concat(msg)});
    });
  }

  handleCreateMessage(e) {
     this.messagesRef.push({
         message: this.state.newMessage,
         roomId: this.state.roomId,
         sendAt: this.props.firebase.database.ServerValue.TIMESTAMP,
         user: this.state.user
     })
     e.preventDefault();
     this.setState({newMessage: ''});
 }

  render() {
    return (
      <section className="message-list">
        {this.state.messages
          .filter(messages => messages.roomId === this.props.activeRoomId)
          .map(messages => (
            <div className="message-subsection" key={messages.key}>
              <div>{messages.username}</div>
              <div>{messages.content}</div>
              <div>{messages.sentAt}</div>
            </div>
          ))}
          <form className="new-message-form" onSubmit={this.handleCreateMessage}>
          <label>
                New Message:
                <input type="text" text={this.state.newMessage} onChange={ (e) => this.handleChange(e) }/>
          </label>
                <button type="submit">Submit</button>
          </form>

      </section>
    );
  }
}

export default MessageList;
