import React, { Component } from 'react';

class MessageList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      messages: [],
      roomId: '',
      newMessage: '',
      user: '',


    }

    this.messagesRef = this.props.firebase.database().ref('messages');
  };

  componentDidMount(){
    this.messagesRef.on('child_added', snapshot => {
      const message = snapshot.val();
      message.key = snapshot.key;
      this.setState({ messages: this.state.messages.concat( message ) })
    });
  }

  handleChange(event) {
    this.setState({
        newMessage: event.target.value,
        roomId: this.props.activeRoom.key,
        user: "user",
    });
 }

   createMessage(e) {
    this.messagesRef.push({
         message: this.state.newMessage,
         roomId: this.state.roomId,
         sendAt: this.props.firebase.database.ServerValue.TIMESTAMP,
         user: this.state.user
     })
     e.preventDefault();
     this.setState({newMessage: ''});
 }

  render() {
    return (
      <section className={this.props.activeRoom}>
      {
        this.state.messages.map( (message, index) =>
        <p className="messages" key={index} > {message.name}</p>,
        console.log(this.state.messages)
        )
      }

      <form onSubmit={(e) => this.createMessage(e)}>
            <br />
            Create New Message:
            <br />
            <input type="text" value={this.state.newMessage} onChange={ e => this.handleMessage(e) }/>
          <br />
          <input type="submit" value="Submit" />
        </form>

        { this.state.messagesList.map( message =>
                  {if (message.roomId === this.props.activeRoom.key) {
                      return <h4 key={message.key}>{message.message}</h4>
                    }
                    return null;}
              )}
              
        </section>
        );
      }
    }

export default MessageList;
