import React, { Component } from 'react';

import fire from '../App';


class User extends Component {
  constructor(props) {
    super(props);
    this.loginEmail = this.loginEmail.bind(this)
    this.loginEmail = this.loginEmail.bind(this)
    this.signInGoogle = this.signInGoogle.bind(this)
    this.signOutGoogle = this.signOutGoogle.bind(this)
  }

  authListener() {
    fire.auth().onAuthStateChanged((user) => {
      console.log(user);
      if (user) {
        this.setState({ user });
        localStorage.setItem('user', user.uid);
      } else {
        this.setState({ user:null });
        localStorage.removeItem('user');
      }
    });
  }

  loginEmail(email, password) {
    return this.auth.signInWithEmailAndPassword(email, password);
  }

  loginEmail() {
    return this.auth.signOut();
  }

  async register(name, email, password) {
    await this.auth.createUserWithEmailAndPassword(email, password);
    return this.auth.currentUser.updateProfile({
      displayName: name
    });
  }


  signInGoogle() {
    const provider = new this.props.firebase.auth.GoogleAuthProvider();
    this.props.firebase.auth().signInWithPopup(provider).then((result) => {
    const user = result.user;
    this.props.setUser(user);
   })
   .catch(function(error) {
       console.log(error);
    });
 }

  signOutGoogle() {
    this.props.firebase.auth().signOut().then(() => {
    this.props.setUser(null);
  })
  .catch(function(error) {
       console.log(error);
  });
}

  componentDidMount() {
    this.props.firebase.auth().onAuthStateChanged(user => {
      if (user) {
        this.setState({user})
      }
    })
  }

  render() {
    return(
      <div className="user">
        <section className="btn-group">
         <button type="button" onClick={this.signInGoogle}>Sign In with Google</button>
         <button type="button" onClick={this.signOutGoogle}>Sign Out</button>

         <section className="display-username">
            Sign In As:
            {this.props.user ? this.props.user.displayName : 'Guest'}
          </section>
          </section>
       </div>

  )
}
}

export default User;
